const initialState = {
    categories: [
        {
            id: 1,
            name: "CSPM Executive Dashboard",
            widgets: [
                { id: 1, name: "Cloud Accounts", content: "Cloud accounts are user profiles or entities created within cloud service platforms (like AWS, Azure, or Google Cloud) that allow access to cloud resources and services. Managing cloud accounts effectively is crucial for maintaining security, ensuring proper access controls, and optimizing resource usage." },
                { id: 2, name: "Cloud Account Risk Assessment", content: "Cloud account risk assessment involves evaluating the potential security threats and vulnerabilities associated with cloud accounts. This process typically includes identifying misconfigurations, assessing permissions and access controls, and monitoring for unusual activities. Regular assessments help in mitigating risks and ensuring that cloud accounts are secure." }
            ]
        },
        {
            id: 2,
            name: "CWPP Dashboard",
            widgets: [
                { id: 3, name: "Top 5 Namespace Specific Alerts", content: "Namespace-specific alerts are notifications related to events or issues within a particular namespace in a cloud environment. The top 5 namespace-specific alerts might include:Unauthorized Access Attempts - Alerts triggered by failed login attempts or access from unrecognized IP addresses.Resource Overutilization - Notifications about excessive resource usage that may impact performance or cost.Configuration Changes - Alerts for changes in configurations that could affect security or compliance.Unusual Traffic Patterns - Detection of unexpected traffic that could indicate a security breach or DDoS attack.Compliance Violations - Notifications about deviations from established security and compliance policies." }
            ]
        },
        {
            id: 3,
            name: "Registry Scan",
            widgets: [
                { id: 4, name: "Image Risk Assessment", content: "Image risk assessment focuses on evaluating the security and integrity of container images used in a cloud environment. This involves scanning images for vulnerabilities, checking for outdated components, and ensuring that images adhere to security policies. Proper image risk assessment helps in preventing potential security breaches and ensuring that only secure images are deployed." },
                { id: 5, name: "Image Secunty Issues", content: "Random text 3" }

            ]
        }
    ]
};

const widgetReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_WIDGET':
            return {
                ...state,
                categories: state.categories.map(category =>
                    category.id === parseInt(action.payload.categoryId)
                        ? {
                            ...category,
                            widgets: [
                                ...category.widgets,
                                {
                                    id: category.widgets.length + 1,
                                    name: action.payload.name,
                                    content: action.payload.content
                                }
                            ]
                        }
                        : category
                )
            };
        case 'REMOVE_WIDGET':
            return {
                ...state,
                categories: state.categories.map(category =>
                    category.id === action.payload.categoryId
                        ? {
                            ...category,
                            widgets: category.widgets.filter(widget => widget.id !== action.payload.widgetId)
                        }
                        : category
                )
            };
        default:
            return state;
    }
};

export default widgetReducer;
