const initialState = {
    categories: [
      {
        id: 1,
        name: "CSPM Executive Dashboard",
        widgets: [
          { id: 1, name: "Cloud Accounts", content: "Random text 1" },
          { id: 2, name: "Cloud Account Risk Assessment", content: "Random text 2" }
        ]
      },
      {
        id: 2,
        name: "CWPP Dashboard",
        widgets: [
          { id: 3, name: "Top 5 Namespace Specific Alerts", content: "Random text 3" }
        ]
      },
      {
        id: 3,
        name: "Registry Scan",
        widgets: [
          { id: 4, name: "Image Risk Assessment", content: "Random text 3" }
          
        ]
      }
    ]
  };
  
  const widgetReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'ADD_WIDGET':
        return {
          ...state,
          categories: state.categories.map(category =>
            category.id === parseInt(action.payload.categoryId)
              ? {
                  ...category,
                  widgets: [
                    ...category.widgets,
                    {
                      id: category.widgets.length + 1,
                      name: action.payload.name,
                      content: action.payload.content
                    }
                  ]
                }
              : category
          )
        };
      case 'REMOVE_WIDGET':
        return {
          ...state,
          categories: state.categories.map(category =>
            category.id === action.payload.categoryId
              ? {
                  ...category,
                  widgets: category.widgets.filter(widget => widget.id !== action.payload.widgetId)
                }
              : category
          )
        };
      default:
        return state;
    }
  };
  
  export default widgetReducer;
